import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

constructor() {
 super();

 this.state = {
   name: '',
 }

}

  render() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <p>
        Hi {this.state.name}
        </p>

        <input onChange={() => {
          this.setState({name: this})
        } } type="text" value="james" name="nameChange" id="nameChange" />

        <button onClick={() => { 
          this.setState({ name: 'Jack' })
         }}>
        Change Name
      </button>

      </header>
    </div>
  );
  }
}

export default App;
